import React from 'react';
import { connect } from 'react-redux';

const addItem = (e) => {};

const AddTodo = ({ dispatch }) => {
  let input;

  return (
    <div>
      <form onSubmit={this.addItem}>
        <input ref={(node) => (input = node)} />
        <button type="submit">Add Todo</button>
      </form>
    </div>
  );
};

export default connect()(AddTodo);
