import React from 'react';
import { connect } from 'react-redux';
import { addTodo } from '../actions';

const AddTodo = ({ dispatch, onSubmit }) => {
  let input;

  return (
    <div>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          if (!input.value.trim()) {
            return;
          }
          // dispatch(addTodo(input.value));
          addTextToList();
          input.value = '';
        }}
      >
        <input ref={(node) => (input = node)} />
        <button type="submit">Add Todo</button>
      </form>
    </div>
  );
};

const addTextToList = (dispatch) => ({
  onSubmit: () => dispatch(addTodo('hi')),
});

export default connect(addTextToList)(AddTodo);
